import React from 'react';
import data from '../data/data';
import Cards from './Cards';

// const images = [
//  1,2,3,4];


class Gallery extends React.Component {
constructor(props){
    super(props);
    this.state={
        properties:data.properties,
        property:data.properties[0]
    
    }
}
    
nextImage = () =>{
const newIndex = this.state.property.index+1;
this.setState ({

    property:data.properties[newIndex]
})
}

prevImage = () =>{
    const newIndex = this.state.property.index-1;
    this.setState ({
    

        property:data.properties[newIndex]
    })  
}

    render() { 
   
    const {properties,property,index} = this.state;
  
 
        return ( 
            <div>
       <button  className ="img-btn" onClick ={()=> this.prevImage()} disabled ={property.index === 0}>Prev</button>
       <button className ="img-btn" onClick ={()=> this.nextImage()} disabled ={property.index === data.properties.length-1}>Next</button>

       <Cards property={property} />
</div>
 
         );
    }
}
 
export default Gallery;
