import React from 'react';
import data from '../data/data';
import Cards from './Cards';
// const images = [
//  1,2,3,4];


class Gallery extends React.Component {
constructor(props){
    super(props);
    this.state={
        properties:data.properties,
        property:data.properties[0],
    
    }
}
    
nextImage = () =>{
const newIndex = this.state.property.index+1;
this.setState ({

    property:data.properties[newIndex]
})
}

prevImage = () =>{
    const newIndex = this.state.property.index-1;
    this.setState ({
    

        property:data.properties[newIndex]
    })  
}

    render() { 
   
    const {properties,property} = this.state;
    const img = properties.map((m,id)=>{
        return <img key={id}src={require(`../images/${m.picture}.jpg`)} alt=""/>
    })
 
    console.log(properties);
        return ( 
        <div className="gallery">
        <div className="cards-slider">
        <div className="cards-slider-wrapper">
        
        <div className="card" style="width: 18rem;">
  {img}
  <div className="card-body">
    <h5 className="card-title">Card title</h5>
    <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
    <a href="#" className="btn btn-primary">Go somewhere</a>
  </div>
  </div>








        </div>
        </div>    
        
 
 <button onClick={()=>this.nextImage()} disabled={property.index === data.properties.length-1}>Next</button>
 <button onClick={()=>this.prevImage()} disabled={property.index === 0}>Prev</button>

 
 </div>
         );
    }
}
 
export default Gallery;
