import React from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBIcon, MDBInput } from 'mdbreact';


class ContactMe extends React.Component{
  constructor(){
    super();
    
    this.state = {
      name : '',
      email : '',
      subject : '',
      message : ''
    }
  this.baseState = this.state;
  }
handleChange = e =>{
  this.setState({[e.target.name]:e.target.value})
}
resetForm = ()=>{
    this.setState(this.baseState);
  }

  render(){
    return (
      
        <div className="contact_me">
        <h3>You can reach me</h3>

        <div className="contact-content">
       
       {/* content --one */}
        <div className="form">
      <MDBContainer>
        <MDBRow>
          <MDBCol md="6">
            <form className="contact-form">
              <div className="contact">
              
                <MDBInput
                  label="name"
                  icon="user"
                  name="name"
                  group
                  type="text"
                  validate
                  error="wrong"
                  success="right" onChange={this.handleChange}
                  value={this.state.name}
                />
                <MDBInput
                  label="email"
                  icon="envelope"
                  name="email"
                  group
                  type="email"
                  validate
                  error="wrong"
                  success="right" onChange={this.handleChange}
                  value={this.state.email}
                />
                <MDBInput
                  label="subject"
                  name="subject"
                  icon="tag"
                  group
                  type="text"
                  validate
                  error="wrong"
                  success="right" onChange={this.handleChange}
                  value={this.state.subject}
                />
                <MDBInput
                  type="textarea"
                  name="message"
                  rows="2"
                  label="message"
                  icon="pencil-alt" onChange={this.handleChange}
                  value={this.state.tex}
                />
              </div>
              <div className="text-center">
                <MDBBtn  className ="btn btn-default"  size="sm">
                  Send <MDBIcon far icon="paper-plane" className="ml-1" />
                </MDBBtn>
                <MDBBtn  onClick={this.resetForm} className ="btn btn-default" size="sm" >
                  Clear <MDBIcon far icon="paper-plane" className="ml-1" />
                </MDBBtn>
              </div>
            </form>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
      </div>
      {/* end of content one */}
      {/* content two */}
      <div className="details">
    <p><i className="fas fa-map-marker-alt"></i> The Reserve at Walnut creek Apartments</p>
    <p><i className="fas fa-phone"></i>54287687</p>
    <p><i className="fas fa-envelope"></i>bakemore@gmail.com</p>
      </div>
      {/* end of content two */}
      </div>  {/*end of contact-content */}
      </div> 
    //   end of contact
    );
  }
  }


export default ContactMe;
