import React from 'react';
const weekdays = [
"Sunday",
"Monday",
"Tuesday",
"Wednesday",
"Thursday",
"Friday",
"Saturday"
];
class  Month extends React.Component {

    render() { 
        const weekDayList = weekdays.map((weekday) =>{
            return (  
                <Weekday key={weekday} title={abbreviationForWeekdays(weekday)} label={weekday} />
                );
        });
        return ( 
            <div>
                <React.Fragment>
                    <div className="weekdayContainer">{weekDayList}</div>
                </React.Fragment>
            </div>
         );
    }

    abbreviationForWeekdays = (weekday) =>{
return weekday.sunbstring(0,2);
}

}
 
export default Month;
