import React from 'react';





const modals = {
    position:'fixed',
    top:0,
    bottom:0,
    right:0,
    left:0,
    backgroundColor: 'rgba(0, 0, 0, 0.3)',
    padding:50
}
const modalStyle = {
    backgroundColor: '#fff',
borderRadius: 5,
maxWidth:500,
minHeight: 300,
margin:'0 auto',
padding:30,
position: 'relative'
}

class Modal extends React.Component {
     
    onClose=(e)=>{
      this.props.onClose && this.props.onClose(e);
    }
    render() { 
        if(!this.props.show){
            return null;
        }
        return (  
            <div>
        
            {this.props.children}
            <div>
                <button onClick={(e)=> {this.onClose(e)}}>Close</button>
                
            </div>
            </div>
        
        );
    }
}
 
export default Modal;
