import React from 'react';
import moment from 'moment';


class  Calendar extends React.Component {
    constructor(props){
        super(props);
        this.width = props.width || '360px';
        this.style = props.style || {};
        this.style.width = this.width;
        
    this.state = {
        dateContext: moment(),
        today:moment(),
        showMonthPopUp : false,
        showYearPopUp: false
    }

    }

weekdays = moment.weekdays();
weekdayShort = moment.weekdaysShort();
months = moment.months();

year = () =>{
    return this.state.dateContext.format("Y");
}
month = () =>{
    return this.state.dateContext.format("MMMM");
}
daysInMonth = () =>{
    return this.state.dateContext.daysInMonth();
}
CurrentDate = () =>{
    return this.state.dateContext.get("date");
}
CurrentDay = () =>{
    return this.state.dateContext.format("D");
}

firstDayOfMonth = () => {
let dateContext = this.state.dateContext;
let firstDay = moment(dateContext).startOf('month').format('d');
return firstDay;

}
SelectList = (props)=>{
let popup = this.props.data.map((data)=>{
    return (
<div key={data}>
<a href="#">{data}</a>
</div>
    );
});

return (
<div className="month-popup">
{popup}
</div>
);


}

MonthNav = ()=>{
return (
<span className="lable-month">
{this.month()}
<this.SelectList data={this.month} />
</span>
);
}













render (){ 
    let weekdays = this.weekdayShort.map((day) =>{
        return (<td key={day} className="week-day">{day} </td>
        )
    });

let blanks = [];
for(let i=0; i < this.firstDayOfMonth(); i++){
    blanks.push(<td key={i * 80}className="emptySlots">{""}</td>)
}

console.log("Blanks:", blanks);

let daysInMonth = [];
for(let d = 1; d < this.daysInMonth(); d++){
    let className = (d === this.CurrentDate() ? 'day current-day' : 'day');
    daysInMonth.push(
        <td key={d} className={className}>
        <span>{d}</span>
        </td>
    );
}
console.log(daysInMonth);

var totalSlots = [...blanks, ...daysInMonth];
let rows = [];
let cells = [];

totalSlots.forEach((row,i) =>{
    if((i % 7)  !== 0 ){
        cells.push(row);
    }
    else{
        let insertRow = cells.slice();
        rows.push(insertRow);
        cells =[];
        cells.push(row);
    }
    if(i === totalSlots.length-1){
        let insertRow = cells.slice();
        rows.push(insertRow);
    }
});

let trElem = rows.map((d,i) =>{
    return (
        <tr key={i*100}>
{d}
        </tr>
    );
})














    return(

<div className="calendar-container">

<table className="calendar">
    
        <thead >
<tr className="calendar-header">
<td colSpan="5">
<this.MonthNav />
</td>
</tr>
        </thead>
        <tbody>
            <tr>
{weekdays}
</tr>
{trElem}
        </tbody>
    
</table>

</div>
    

    




    );
}
}
 export default Calendar;
