import React from 'react';
import data from '../data/data';
import Cards from './Cards';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCol } from 'mdbreact';

// const images = [
//  1,2,3,4];


class Gallery extends React.Component {
constructor(props){
    super(props);
    this.state={
        properties:data.properties,
        property:data.properties[0]
    
    }
}
    
nextImage = () =>{
const newIndex = this.state.property.index+1;
this.setState ({

    property:data.properties[newIndex]
})
}

prevImage = () =>{
    const newIndex = this.state.property.index-1;
    this.setState ({
    

        property:data.properties[newIndex]
    })  
}

    render() { 
   
    const {properties,property,index} = this.state;
     const img = properties.map((m,id)=>{
        return <div>
        {/* <MDBCardImage className="img-fluid" src={require(`../images/${m.picture}.jpg`)} waves /> */}

        </div>
    })
 
        return ( 
            <div>
       <button onClick ={()=> this.nextImage()} disabled ={property.index === data.properties.length-1}>Next</button>
       <button onClick ={()=> this.prevImage()} disabled ={property.index === 0}>Prev</button>

       <MDBCol>
       <MDBCard  id={`card-${index}`} style={{ width: "22rem" }}>
       <MDBCardImage className="img-fluid" src={require(`../images/${m.picture}.jpg`)} waves />

        <MDBCardBody>
          <MDBCardTitle>Card title</MDBCardTitle>
         <span className="index">{index+1}</span>
          <MDBBtn href="#">MDBBtn</MDBBtn>
        </MDBCardBody>
      </MDBCard>
    </MDBCol>
</div>
 
         );
    }
}
 
export default Gallery;
