import React from 'react';

class Modal extends React.Component {
    
    onClose
    render() { 
        if(!this.props.show){
            return null;
        }
        return (  
            <div>
            {this.props.children}
            <div>
                <button onClose={(e)=> onClose(e)}>Close</button>
            </div>
            </div>
        );
    }
}
 
export default Modal;
